import React, { useState } from 'react';

const StoreRegistrationPage: React.FC = () => {
    const [storeData, setStoreData] = useState({
        name: '',
        address: '',
        hours: '',
        description: '',
    });

    const [items, setItems] = useState([
        { name: '', price: '', description: '', tags: '', image: null as File | null }
    ]);

    const handleStoreChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setStoreData({ ...storeData, [e.target.name]: e.target.value });
    };

    const handleItemChange = (index: number, e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const newItems = [...items];
        const { name, value, files } = e.target as HTMLInputElement;
        newItems[index] = { ...newItems[index], [name]: files ? files[0] : value };
        setItems(newItems);
    };

    const addItem = () => {
        setItems([...items, { name: '', price: '', description: '', tags: '', image: null }]);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        // Submit store and item data to your API
        console.log('Store:', storeData);
        console.log('Items:', items);
    };

    return (
        <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10 font-inter">
        <h1 className="text-2xl font-bold text-brand-charcoal mb-4">Register Your Storefront</h1>
        <form onSubmit={handleSubmit} className="space-y-6">
            {/* Store Info */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input type="text" name="name" placeholder="Store Name" value={storeData.name} onChange={handleStoreChange} className="p-3 border rounded" required />
            <input type="text" name="address" placeholder="Address" value={storeData.address} onChange={handleStoreChange} className="p-3 border rounded" required />
            <input type="text" name="hours" placeholder="Operating Hours" value={storeData.hours} onChange={handleStoreChange} className="p-3 border rounded" />
            </div>
            <textarea name="description" placeholder="Store Description" value={storeData.description} onChange={handleStoreChange} className="w-full p-3 border rounded" rows={3} />

            {/* Items Upload */}
            <h2 className="text-lg font-semibold text-brand-charcoal mt-6">Items to List</h2>
            {items.map((item, index) => (
            <div key={index} className="border p-4 rounded space-y-2">
                <input type="text" name="name" placeholder="Item Name" value={item.name} onChange={e => handleItemChange(index, e)} className="w-full p-2 border rounded" required />
                <input type="number" name="price" placeholder="Price" value={item.price} onChange={e => handleItemChange(index, e)} className="w-full p-2 border rounded" required />
                <textarea name="description" placeholder="Description" value={item.description} onChange={e => handleItemChange(index, e)} className="w-full p-2 border rounded" />
                <input type="text" name="tags" placeholder="Tags (comma-separated)" value={item.tags} onChange={e => handleItemChange(index, e)} className="w-full p-2 border rounded" />
                <input type="file" name="image" accept="image/*" onChange={e => handleItemChange(index, e)} className="w-full" />
            </div>
            ))}
            <button type="button" onClick={addItem} className="text-sm text-brand-orange hover:underline">+ Add another item</button>

            <button type="submit" className="mt-6 bg-brand-orange text-white px-6 py-2 rounded hover:bg-orange-600 transition">Submit Storefront</button>
        </form>
        </div>
    );
};

export default StoreRegistrationPage;
