import React, { useState, useEffect } from 'react';
import { X } from 'lucide-react';

interface StoreRegistrationPageProps {
    isOpen: boolean;
    onClose: () => void;
}

const StoreRegistrationPage: React.FC<StoreRegistrationPageProps> = ({ isOpen, onClose }) => {
    const [storeData, setStoreData] = useState({
        name: '',
        address: '',
        hours: '',
        description: '',
    });

    useEffect(() => {
        if (isOpen) {
        document.body.style.overflow = 'hidden';
        } else {
        document.body.style.overflow = 'unset';
        }
        return () => {
        document.body.style.overflow = 'unset';
        };
    }, [isOpen]);

    if (!isOpen) return null;

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setStoreData({ ...storeData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Store submitted:', storeData);
        onClose();
    };

    return (
        <div
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 p-4"
            role="dialog"
            aria-modal="true"
            onClick={onClose}
        >
            <div
                className="bg-white rounded-lg shadow-xl w-full max-w-xl relative overflow-y-auto max-h-[90vh] font-inter"
                onClick={(e) => e.stopPropagation()}
            >
                <button
                    onClick={onClose}
                    className="absolute top-4 right-4 text-gray-400 hover:text-gray-600"
                    aria-label="Close"
                >
                    <X size={24} />
                </button>
                <div className="p-6">
                    <h2 className="text-2xl font-bold text-brand-charcoal mb-4">Register Your Storefront</h2>
                    <form onSubmit={handleSubmit} className="space-y-6">
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <input
                            type="text"
                            name="name"
                            placeholder="Store Name"
                            value={storeData.name}
                            onChange={handleChange}
                            className="p-3 border rounded w-full"
                            required
                        />
                        <input
                            type="text"
                            name="address"
                            placeholder="Address"
                            value={storeData.address}
                            onChange={handleChange}
                            className="p-3 border rounded w-full"
                            required
                        />
                        <input
                            type="text"
                            name="hours"
                            placeholder="Operating Hours"
                            value={storeData.hours}
                            onChange={handleChange}
                            className="p-3 border rounded w-full"
                        />
                        </div>
                        <textarea
                            name="description"
                            placeholder="Store Description"
                            value={storeData.description}
                            onChange={handleChange}
                            className="w-full p-3 border rounded"
                            rows={3}
                        />
                        <button
                            type="submit"
                            className="mt-4 bg-brand-orange text-white px-6 py-2 rounded hover:bg-orange-600 transition"
                        >
                            Submit Storefront
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};
export default StoreRegistrationPage;
